<?php

use Nette\IDebugPanel,
 Doctrine\ORM\EntityManager,
 Nette\Environment,
 Doctrine\ORM\Tools\SchemaTool;

/**
 * @author David MorÃ¡vek
 */
class SchemaPanel implements IDebugPanel
{



	/**
	 * @param EntityManager $em
	 */
	public function __construct($em)
	{
		$this->processRequest($em);
	}



	/**
	 * IDebugPanel
	 *
	 * @return string
	 */
	public function getTab()
	{
		return '<img src="data:image/png;base64,">SchemaTool';
	}



	/**
	 * IDebugPanel
	 *
	 * @return string
	 */
	public function getPanel()
	{
		ob_start();
		require_once __DIR__ . '/schema.panel.phtml';
		return ob_get_clean();
	}



	/**
	 * IDebugPanel
	 *
	 * @return string
	 */
	public function getId()
	{
		return 'schema-tool';
	}



	/**
	 * Ajax request process
	 * @var EntityManager
	 */
	public function processRequest($em)
	{
		$request = Environment::getHttpRequest();

		if ($request->isPost() && $request->isAjax() && $request->getHeader('X-Schema-Client')) {

			$cmd = file_get_contents('php://input', TRUE);
			$schemaTool = new SchemaTool($em);
			$metadatas = $em->getMetadataFactory()->getAllMetadata();

			try {
				switch ($cmd) {
					case 'create':
						$schemaTool->createSchema($metadatas);
						break;
					case 'update':
						$schemaTool->updateSchema($metadatas);
						break;
					case 'drop':
						$schemaTool->dropSchema($metadatas);
						break;

					default:
						throw new InvalidArgumentException('Invalid argument!');
						break;
				}
				$message['text'] = ucfirst($cmd) . ' query was successfully executed';
				$message['cls'] = 'success';
			} catch (Exception $e) {
				$message['text'] = $e->getMessage();
				$message['cls'] = 'error';
			}
			$response = new \Nette\Application\JsonResponse($message);
			$response->send();
			exit;
		}
	}



	/**
	 * @param EntityManager $em
	 */
	public static function register(EntityManager $em = NULL)
	{
		Nette\Debug::addPanel(new static($em ? $em : Environment::getService('Doctrine\ORM\EntityManager')));
	}

}